async function fetchAlbums(){await fetch(url).then(e=>e.json()).then(e=>jsondata=e).then(e=>appendData(e))}function appendData(e){for(let t=0;t<listSize;t++)listElement.innerHTML+=`<div id="Id${e[t].id}" onClick=changeData(${e[t].id}) class="list__elem showId">${e[t].id}</div>`}function changeData(e){let t=`#Id${e}`,s=document.querySelector(t);return s.classList.contains("showId")?(s.innerHTML=`${jsondata[e-1].title}`,s.classList.remove("showId"),void s.classList.add("showTitle")):s.classList.contains("showTitle")?(s.innerHTML=`${jsondata[e-1].id}`,s.classList.add("showId"),void s.classList.remove("showTitle")):void 0}function changeViewStyle(){document.querySelector("#changeViewStyleBtn").checked?document.querySelector(".list").classList.add("list--even"):document.querySelector(".list").classList.remove("list--even")}let listElement=document.querySelector(".list");const url="https://jsonplaceholder.typicode.com/albums",listSize=10;let jsondata;fetchAlbums();